<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => '5.4.37',
    'version' => '5.4.37.0',
    'aliases' => 
    array (
    ),
    'reference' => 'a701c1159e7a000861a1b2094ed392f752343e72',
    'name' => 'gantry/grav',
  ),
  'versions' => 
  array (
    'gantry/grav' => 
    array (
      'pretty_version' => '5.4.37',
      'version' => '5.4.37.0',
      'aliases' => 
      array (
